import React, {useState} from 'react'
import Card from './Card'

const User = (props) => {
console.log('props',props)

const draw1Card = async () => {
    const fetchDeck = await fetch(
      `https://deckofcardsapi.com/api/deck/${props.deckID}/draw/?count=1`
    );
    const jsonDeck = await fetchDeck.json();

    const cardCode = jsonDeck.cards[0].code;
    const cardImg = jsonDeck.cards[0].image;


    const fetchPile = await fetch(
      `https://deckofcardsapi.com/api/deck/${deckID}/pile/player/add/?cards=${cardCode}`
    );
    const pileinfo = await fetchPile.json();

    const fetchCard = await fetch(
      `https://deckofcardsapi.com/api/deck/${deckID}/pile/player/list/`
    );
    const handInfo = await fetchCard.json();
    const cardInfo = handInfo.piles.user.cards;
    console.log("handinfo", handInfo);
    console.log("cardinfo", cardInfo);
    const cardsArr = cardinfo.map(card => {
      return(
        <Card
        key={card.code}
        image={card.image}
        value={card.value}
        suit={card.suit}
        />)
      })




    return(
        <div>
        <p>Player</p>
        <button onClick={'handleDeal'}>Deal</button>
        <button onClick={() => {props.handleHit(draw1Card())}}>Hit</button>
        <button onClick={'handleStay'}>Stay</button>
        {cardsArr}
        </div>
    )
}
}
export default User
