import React, {useState} from 'react'
import Card from './Card'
import './User.css'

const User = (props) => {
// console.log('props',props)

const fetchShuffle = async () => {
    const res = await fetch(
    `https://deckofcardsapi.com/api/deck/${props.deckID}/shuffle/`
  );
const jsonShuffle = await res.json();
console.log('shuffle result', jsonShuffle)
}

const [cards, setCards] = useState([])

const draw1Card = async (player) => {
    const fetchDeck = await fetch(
      `https://deckofcardsapi.com/api/deck/${props.deckID}/draw/?count=1`
    );
    const jsonDeck = await fetchDeck.json();

    const cardCode = jsonDeck.cards[0].code;

    const fetchPile = await fetch(
      `https://deckofcardsapi.com/api/deck/${props.deckID}/pile/player/add/?cards=${cardCode}`
    );
    const pileinfo = await fetchPile.json();

    const fetchCard = await fetch(
      `https://deckofcardsapi.com/api/deck/${props.deckID}/pile/player/list/`
    );
    const handInfo = await fetchCard.json();
    const cardInfo = handInfo.piles.player.cards;
    // console.log("handinfo", handInfo);
    console.log("cardinfo", cardInfo);
    setCards(cardInfo)
 }   


    const cardsArr = cards.map(card => {
      return(
        <Card                       //maybe we copy paste this back to table and change card to user and dealer
        key={card.code}
        image={card.image}
        value={card.value}
        suit={card.suit}
        />)
      })

    const handleDeal = async () => {
       console.log('this is the click shuffle',fetchShuffle())

        // for(let i = 0; i < 3; i++){
        //     draw1Card('user')
        // }
        draw1Card('user')
        setTimeout(() => {
            console.log(
                'inside timeout'
            )
            draw1Card('user')}, 1000); 
    }



    return(
        <div>
        <p>Player</p>
        <button onClick={handleDeal}>Deal</button>
        <button onClick={() => {props.handleHit(draw1Card('user'))}}>Hit</button>
        <button onClick={'handleStay'}>Stay</button>
        <div className='hand'>
            {cardsArr}
        </div>

        </div>
    )

}
export default User
